import logo from './logo.svg';
import './App.css';
import SearchCritera from './Component/SearchCriteria';
import React, {useState, useEffect} from 'react';

function App() {

  const[defintion, setDefinition] = useState([]);
  const[standard, setStandard] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    fetch('https://localhost:7195/api/SearchCriteria/Get')
      .then((res) => res.json())
      .then((data) => {
        setDefinition(data.videoDefinitions);
        setStandard(data.setStandard);
        console.log(searchCriteria);
      })
      .catch((err) => {
        console.log(err);
      })
      .finally(() => {
        setLoading(false);
      });
  }, {});

  if (loading) {
    return <p>Data is loading...</p>;
  }

  return (
    <div>
        <ul>
      {defintion.map((item) => (
        <li key={item.id}>{item.value}</li>
      ))}
    </ul>
    <ul>
      {standard.map((item) => (
        <li key={item.id}>{item.value}</li>
      ))}
    </ul>
  </div>
  );

}

export default App;
