import React from 'react';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import { Select } from '@material-ui/core';

function VideoDefinition (props) {
 const [videoDefinition, setVideoDefinition] = React.useState('');

  const handleChange = (event) => {
    setVideoDefinition(event.target.value);
  };

    return(
        <Box>
        <FormControl  fullWidth>
        <InputLabel>Video Definition</InputLabel>
        <Select id="videoDefinitionSelector" label="VideoDefinition"
          onChange={handleChange}>
              <List>
              {props.definitions.map(item => <MenuItem value = {item.value} key = {item.id}/>)}
              </List>           
        </Select>
    </FormControl>
    </Box>
    );

};

export default VideoDefinition;
