import React from 'react';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { MenuList } from '@material-ui/core';

function VideoStandard (props) {

  const [videoStandard, setVideoStandard] = React.useState('');

  const handleChange = (event) => {
    setVideoStandard(event.target.value);
  };

    return(
        <Box>
        <FormControl fullWidth>
        <InputLabel>Video VideoStandard</InputLabel>
        <Select id="videoStandardSelector"  label="VideoStandard"
          onChange={handleChange}>
        <MenuList>
        {props.standards.map(item => <MenuItem value = {item.value} key = {item.id}/>)}
        </MenuList>

        </Select>
    </FormControl>
    </Box>
    );

};

export default VideoStandard;
