import { FormControl } from "@mui/material";
import VideoDefinition from "./VideoDefinition";
import VideoStandard from "./VideoStandard";
import React, { useState } from "react";

function SearchCritera (props)
{
    const[vDefintion, updateVDefintion] = useState('');
    const[vStandard, updateVStandard] = useState('');

    function getVideoDefintionHandler(defintion)
    {
        console.log(defintion);
        updateVDefintion(defintion);     
        console.log(vDefintion); 

    }

    function getVideoStandardHandler(standard)
    {
        updateVStandard(standard);

    }

    function CreateVideoClipRequest()
    {
        console.log(vDefintion);
        Event.preventDefault();
        const request = {
            VideoDefinition: vDefintion,
            VideoStandard: vStandard
        }

        console.log(request);
        props.videoHandler(request);
    }

    return(
        <FormControl fullWidth onSubmit={CreateVideoClipRequest}>
            <VideoDefinition 
            definitions = {props.videoDefinitions} 
            videoDefHandler = {getVideoDefintionHandler}>
            </VideoDefinition>
            <VideoStandard 
            standards = {props.videoStandards} 
            videoStdHandler = {getVideoStandardHandler}>
            </VideoStandard>
            <button type='submit'>Add Expense</button>
        </FormControl>
    );
}

export default SearchCritera;
