import { Box} from "@mui/material";
import { DataGrid } from '@mui/x-data-grid'
import React from "react";

function VideoClips (props)
{
    function getTimeDuration(params)
    {
       return `${params.hour} : ${params.row.minutes} : ${params.row.sec} : {${params.row.frames} `;

    }
    console.log(props.videoClips);
    const columns = [
        { field: 'name', headerName: 'Name', width: 150, },
        {
          field: 'description',
          headerName: 'Description',
          width: 150,
          editable: false,
        },
        {
          field: 'endTime',
          headerName: 'TotalDuration',
          width: 110,
          editable: false,
          valueGetter : getTimeDuration,
        },

      ];

    return (
        <Box sx={{ height: 400, width: '100%' }}>
      <DataGrid
        rows={props.videoClips}
        columns={columns}
        getRowId={(row) => row.name} 
        checkboxSelection
        disableSelectionOnClick
      />
    </Box>
    );
}

export default VideoClips;
