import { Box, Button, Grid} from "@mui/material";
import { DataGrid } from '@mui/x-data-grid';
import { Typography } from '@mui/material';
import React from "react";

function VideoClips (props)
{
    const [selected, setSelected] = React.useState([]);
    const [rows, setRows] = React.useState([]);

    if(props !==  undefined)
        setRows(props.videoClips)

    const handleSelectionChange = React.useCallback(
        (selected = []) => {
          setSelected(rows.filter(({ name }) => selected.includes(name)));
        },
        [rows]
      );

    function getTimeDuration(params)
    {
       return `${params.value.hour}:${params.value.minutes}:${params.value.sec}:${params.value.frames}`;
    }

    const DemoDisplayComponent = ({ name, description, endTime }) => (
        <Grid container spacing={1}>
          <Grid item xs={4}>
            <Typography variant="caption">{name}</Typography>
          </Grid>
          <Grid item xs={4}>
            <Typography variant="caption">{description}</Typography>
          </Grid>
          <Grid item xs={4}>
            <Typography variant="caption">{endTime}</Typography>
          </Grid>
        </Grid>
      );

    const columns = [
        { field: 'name', headerName: 'Name', width: 150, },
        {
          field: 'description',
          headerName: 'Description',
          width: 150,
          editable: false,
        },
        {
          field: 'endTime',
          headerName: 'TotalDuration',
          width: 210,
          editable: false,
          valueGetter : getTimeDuration,
        },

      ];

    return (
        <Box sx={{ height: 400, width: '100%' }}>

    <Grid container spacing={2}>
    <DataGrid
        rows={rows}
        columns={columns}
        getRowId={(row) => row.name} 
        checkboxSelection
        disableSelectionOnClick
        onSelectionModelChange={handleSelectionChange}
      />
      <DataGrid>
      <Typography>Selected Items</Typography>
        {selected.map((item) => (
          <DemoDisplayComponent {...item} />
        ))}
      </DataGrid>
    </Grid>    
    </Box>
    );
}

export default VideoClips;
