import React, {useRef} from 'react';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

function VideoStandard (props) {

  const [videoStandard, updateVideoStandard] = useRef('');

  const handleChange = (event) => {
    updateVideoStandard(event.target.value);
    props.videoStdHandler(videoStandard);
  };
    return(
        <Box>
        <FormControl fullWidth>
        <InputLabel>Video VideoStandard</InputLabel>
        <Select id="videoStandardSelector"  value = {videoStandard} label="VideoStandard"
          onChange={handleChange}>
          {props.standards.map(standard => { 
                return (
                    <MenuItem key = {standard.value} value = {standard.value}>{standard.value}</MenuItem>
                );
                })}      
        </Select>
    </FormControl>
    </Box>
    );

};

export default VideoStandard;
