import React ,{useState, useEffect}from 'react';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import { Select } from '@material-ui/core';

function VideoDefinition (props) {
 const [definition, updateDefinition] = useState('');

  const handleChange = (event) => {
    updateDefinition(event.target.value);
    props.videoDefHandler(definition);
    console.log(definition);
  };

  useEffect(() => {
  }, [definition])

  return(
    <Box>
    <FormControl fullWidth>
    <InputLabel>Video Definition</InputLabel>
    <Select id="videoDefinitionSelector"  value = {definition} label="VideoDefinition"
      onChange={handleChange}>
      {props.definitions.map(definition => { 
            return (
                <MenuItem key = {definition.value} value = {definition.value}>{definition.value}</MenuItem>
            );
            })}      
    </Select>
</FormControl>
</Box>
);

};

export default VideoDefinition;
