import { Button, FormControl } from "@mui/material";
import VideoDefinition from "./VideoDefinition";
import VideoStandard from "./VideoStandard";
import React, {useState} from "react";

function SearchCritera (props)
{
    const [videoDefinition, updateVideoDefinition] = useState('');
    const [videoStandard, updateVideoStandard] = useState('');
    function getVideoDefintionHandler(videoDefintion)
    {
        updateVideoDefinition(videoDefinition);

            if(videoDefinition.length > 0 && videoStandard.length >0)
            {
                createVideoClipRequest();
            }

    }

    function getVideoStandardHandler(videoStandard)
    {
        updateVideoStandard(videoStandard);

        if(videoDefinition.length > 0 && videoStandard.length >0)
        {
                createVideoClipRequest();
        }
    }

    function createVideoClipRequest()
    {
        const request = {
            VideoDefinition: videoDefinition,
            VideoStandard: videoStandard
        }
        props.videoHandler(request);
    }

    return(
        <FormControl fullWidth >
            <VideoDefinition 
            definitions = {props.videoDefinitions} 
            videoDefHandler = {getVideoDefintionHandler}>
            </VideoDefinition>
            <VideoStandard 
            standards = {props.videoStandards} 
            videoStdHandler = {getVideoStandardHandler}>
            </VideoStandard>
        </FormControl>
    );
}

export default SearchCritera;
